// src/components/TicketSubmissionForm.js
import React, { useState } from 'react';

const TicketSubmissionForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    duration: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formData.title && formData.description && formData.duration) {
      onSubmit(formData);
      setFormData({ title: '', description: '', duration: '' }); // Clear the form
    }
  };

  return (
    <form onSubmit={handleSubmit} className="ticket-form">
      <h2>Submit a Ticket</h2>
      <div className="form-group">
        <label>Title</label>
        <input
          type="text"
          name="title"
          value={formData.title}
          onChange={handleChange}
          required
        />
      </div>
      <div className="form-group">
        <label>Description</label>
        <textarea
          name="description"
          value={formData.description}
          onChange={handleChange}
          required
        ></textarea>
      </div>
      <div className="form-group">
        <label>Estimated Duration (minutes)</label>
        <input
          type="number"
          name="duration"
          value={formData.duration}
          onChange={handleChange}
          required
          min="10"
        />
      </div>
      <button type="submit" className="submit-btn">Submit Ticket</button>
    </form>
  );
};

export default TicketSubmissionForm;
