import React, { useState } from 'react';

const TicketSubmissionForm = ({ onClose }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [duration, setDuration] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Prepare the data object
    const ticketData = {
      title,
      description,
      duration
    };

    // API call to submit the ticket
    try {
      const response = await fetch('http://localhost:5000/tickets', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`, // Assuming token is stored in localStorage
        },
        body: JSON.stringify(ticketData)
      });
      
      const data = await response.json();
      if (response.ok) {
        console.log('Ticket submitted successfully:', data);
        onClose(); // Close the form/modal on successful submission
      } else {
        console.error('Failed to submit ticket:', data.error);
      }
    } catch (error) {
      console.error('Error submitting ticket:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Title</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Description</label>
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Estimated Duration (minutes)</label>
        <input
          type="number"
          value={duration}
          onChange={(e) => setDuration(e.target.value)}
          required
        />
      </div>
      <button type="submit">Submit Ticket</button>
    </form>
  );
};

export default TicketSubmissionForm;
