// src/components/AdminDashboard.js
import React, { useEffect, useState } from 'react';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [pendingAccounts, setPendingAccounts] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchPendingAccounts();
  }, []);

  const fetchPendingAccounts = async () => {
    try {
      const response = await fetch('http://localhost:5000/admin/pending-accounts', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
      });

      const data = await response.json();
      if (response.ok) {
        setPendingAccounts(data);
      } else {
        setMessage(data.error);
      }
    } catch (error) {
      setMessage('An error occurred. Please try again.');
    }
  };

  const handleApprove = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/admin/approve/${id}`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
      });

      if (response.ok) {
        setPendingAccounts(pendingAccounts.filter((account) => account.id !== id));
      } else {
        setMessage('Failed to approve account.');
      }
    } catch (error) {
      setMessage('An error occurred. Please try again.');
    }
  };

  const handleDeny = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/admin/deny/${id}`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
      });

      if (response.ok) {
        setPendingAccounts(pendingAccounts.filter((account) => account.id !== id));
      } else {
        setMessage('Failed to deny account.');
      }
    } catch (error) {
      setMessage('An error occurred. Please try again.');
    }
  };

  return (
    <div className="admin-dashboard-container">
      <h1>Admin Dashboard</h1>
      <h2>Pending Accounts</h2>
      {message && <p>{message}</p>}
      <table>
        <thead>
          <tr>
            <th>Username</th>
            <th>Email</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {pendingAccounts.map((account) => (
            <tr key={account.id}>
              <td>{account.username}</td>
              <td>{account.email}</td>
              <td>{account.role}</td>
              <td>
                <button onClick={() => handleApprove(account.id)}>Approve</button>
                <button onClick={() => handleDeny(account.id)}>Deny</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminDashboard;
