import React, { useState, useEffect } from 'react';
import './ITHelpersList.css';  // Ensure you have the CSS file in the same directory

function ITHelpersList({ onSelectHelper }) {
  const [helpers, setHelpers] = useState([]);

  useEffect(() => {
    async function fetchHelpers() {
      const response = await fetch('http://localhost:5000/auth/it-helpers', {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
      });
      if (response.ok) {
        const data = await response.json();
        setHelpers(data);
      }
    }

    fetchHelpers();
  }, []);

  return (
    <div className="it-helpers-list">
      <ul>
        {helpers.map(helper => (
          <button key={helper.id} onClick={() => onSelectHelper(helper.id)} className="helper-button">
            {helper.username}
          </button>
        ))}
      </ul>
    </div>
  );
}

export default ITHelpersList;
