// src/components/UserPage.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './UserPage.css';
import TicketSubmissionForm from './TicketSubmissionForm'; // Ensure this is correctly imported
import Modal from './Modal'; // Ensure Modal component is available

const UserPage = () => {
  const { username } = useParams();
  const navigate = useNavigate();
  const [role, setRole] = useState('');
  const [showTicketForm, setShowTicketForm] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
    } else {
      fetchUserRole(token);
    }
  }, [navigate]);

  const fetchUserRole = async (token) => {
    try {
      const response = await fetch('http://localhost:5000/auth/userinfo', {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      const data = await response.json();
      if (response.ok) {
        setRole(data.role);
      } else {
        console.error(data.error);
        navigate('/login');
      }
    } catch (error) {
      console.error('An error occurred:', error);
      navigate('/login');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const handleTicketModal = () => setShowTicketForm(!showTicketForm);

  return (
    <div className="user-page-container">
      <div className="sidebar">
        <button onClick={handleLogout}>Logout</button>
        {role === 'ticket_user' && (
          <button onClick={handleTicketModal}>Submit Ticket</button>
        )}
      </div>
      <div className="main-content">
        <h1>{username}</h1>
        <p className="user-role">
          {role === 'it_helper' ? 'IT Helper' : role === 'admin' ? 'Admin' : 'Ticket User'}
        </p>
        {showTicketForm && role === 'ticket_user' && (
          <Modal onClose={handleTicketModal}>
            <TicketSubmissionForm />
          </Modal>
        )}
        {role === 'it_helper' && (
          <div className="calendar-container">
            <Calendar />
          </div>
        )}
        {role === 'admin' && (
          <button className="admin-dashboard-btn" onClick={() => navigate('/admin/dashboard')}>
            Go to Admin Dashboard
          </button>
        )}
      </div>
    </div>
  );
};

export default UserPage;
