import React, { useEffect, useState } from 'react';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [pendingAccounts, setPendingAccounts] = useState([]);
  const [pendingTickets, setPendingTickets] = useState([]);
  const [itHelpers, setItHelpers] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchResources = async () => {
      const fetchPendingAccounts = fetch('http://localhost:5000/auth/admin/pending-accounts', {
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });

      const fetchPendingTickets = fetch('http://localhost:5000/auth/admin/tickets', {
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });

      const fetchItHelpers = fetch('http://localhost:5000/auth/it-helpers', {
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });

      try {
        const responses = await Promise.all([fetchPendingAccounts, fetchPendingTickets, fetchItHelpers]);
        const [accountsData, ticketsData, helpersData] = await Promise.all(responses.map(res => res.json()));

        if (responses[0].ok) setPendingAccounts(accountsData);
        else setMessage(accountsData.error || 'Failed to fetch pending accounts.');

        if (responses[1].ok) setPendingTickets(ticketsData);
        else setMessage(ticketsData.error || 'Failed to fetch pending tickets.');

        if (responses[2].ok) setItHelpers(helpersData);
        else setMessage(helpersData.error || 'Failed to fetch IT helpers.');
      } catch (error) {
        setMessage('An error occurred. Please try again later.');
      }
    };

    fetchResources();
  }, []);

  const approveAccount = async (userId) => {
    // Existing logic for approving accounts
  };

  const denyAccount = async (userId) => {
    // Existing logic for denying accounts
  };

  const handleApproveTicket = async (ticketId, timeEstimate) => {
    // Existing logic for approving tickets
  };

  const handleSelectHelper = (helperId) => {
    // Logic to navigate to IT helper's calendar
  };

  return (
    <div className="admin-dashboard">
      <h1 className="dashboard-title">Admin Dashboard</h1>
      {message && <p className="error-message">{message}</p>}
      
      {/* Existing UI elements for pending accounts and tickets */}

      <h2 className="section-title">IT Helpers</h2>
      <ul className="it-helpers-list">
        {itHelpers.map((helper) => (
          <li key={helper.id} className="it-helper-item">
            <span className="helper-info">{helper.username}</span>
            <button onClick={() => handleSelectHelper(helper.id)}>View Calendar</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDashboard;
