import React, { useState, useEffect } from 'react';
import TicketSubmissionForm from './TicketSubmissionForm';
import './UserDashboard.css';
import { useNavigate } from 'react-router-dom';

const UserDashboard = () => {
  const [message, setMessage] = useState('');
  const [submittedTickets, setSubmittedTickets] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchSubmittedTickets = async () => {
      try {
        const response = await fetch('http://localhost:5000/auth/tickets/user-submitted', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${localStorage.getItem('token')}`,
          },
        });

        const data = await response.json();
        if (response.ok) {
          setSubmittedTickets(data.tickets);
        } else {
          setMessage(data.error);
        }
      } catch (error) {
        setMessage('Failed to fetch submitted tickets. Please try again later.');
      }
    };

    fetchSubmittedTickets();
  }, []);

  const submitTicket = async (ticketData) => {
    try {
      const response = await fetch('http://localhost:5000/auth/tickets', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
        body: JSON.stringify(ticketData),
      });

      const data = await response.json();
      if (response.ok) {
        setMessage('Ticket submitted successfully!');
        setSubmittedTickets([...submittedTickets, data.ticket]);
      } else {
        setMessage(data.error);
      }
    } catch (error) {
      setMessage('An error occurred while submitting the ticket.');
    }
  };

  return (
    <div className="user-dashboard">
      <h1>User Dashboard</h1>
      <div className="ticket-form-container">
        <TicketSubmissionForm onSubmit={submitTicket} />
        {message && <p className="status-message">{message}</p>}
      </div>
      <div className="tickets-list-container">
        <h2>Submitted Tickets</h2>
        <ul>
          {submittedTickets.map((ticket) => (
            <li key={ticket.id}>
              <strong>{ticket.title}</strong> - {ticket.description}
            </li>
          ))}
        </ul>
      </div>
      <button className="logout-btn" onClick={() => { localStorage.removeItem('token'); navigate('/login'); }}>
        Logout
      </button>
    </div>
  );
};

export default UserDashboard;
