// src/components/TicketDetails.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const TicketDetails = () => {
    const { ticketId } = useParams();
    const [ticket, setTicket] = useState(null);
    const [message, setMessage] = useState('');

    useEffect(() => {
        const fetchTicketDetails = async () => {
            try {
                const response = await fetch(`http://localhost:5000/tickets/${ticketId}`, {
                    method: 'GET',
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('token')}`,
                        'Content-Type': 'application/json'
                    }
                });
                const data = await response.json();
                if (response.ok) {
                    setTicket(data);
                } else {
                    setMessage(data.error || 'Failed to load ticket details.');
                }
            } catch (error) {
                setMessage('An error occurred. Please try again later.');
            }
        };

        fetchTicketDetails();
    }, [ticketId]);

    return (
        <div className="ticket-details-page">
            <h1>Ticket Details</h1>
            {ticket ? (
                <div>
                    <h2>{ticket.title}</h2>
                    <p>Description: {ticket.description}</p>
                    <p>Duration: {ticket.duration} minutes</p>
                    {/* Placeholder for chat component */}
                    <p>Chat interface will be here</p>
                </div>
            ) : (
                <p>{message}</p>
            )}
        </div>
    );
};

export default TicketDetails;
