import React, { useState } from 'react';
import ITHelpersList from './ITHelpersList';
import ITHelperCalendar from './ITHelpersCalendar';

function ITHelpersPage() {
  const [selectedHelperId, setSelectedHelperId] = useState(null);

  const handleSelectHelper = (id) => {
    setSelectedHelperId(id);
  };

  return (
    <div className="it-helpers-page">
      <div className="it-helpers-list">
        <h2>IT Helpers</h2>
        <ITHelpersList onSelectHelper={handleSelectHelper} />
      </div>
      <div className="it-helper-calendar">
        {selectedHelperId && (
          <>
            <h2>IT Helper's Calendar</h2>
            <ITHelperCalendar helperId={selectedHelperId} />
          </>
        )}
      </div>
    </div>
  );
}

export default ITHelpersPage;
