// src/components/TicketDetail.js

import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const TicketDetail = () => {
  const { ticketId } = useParams();
  const [ticket, setTicket] = useState(null);
  const [chatMessages, setChatMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [role, setRole] = useState('');

  useEffect(() => {
    const fetchTicketDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5000/auth/tickets/${ticketId}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const data = await response.json();
        if (response.ok) {
          setTicket(data.ticket);
          setChatMessages(data.messages); // Assuming messages are part of the response
          setRole(data.role); // Assuming role info is sent from the backend
        } else {
          console.error('Error fetching ticket details:', data.error);
        }
      } catch (error) {
        console.error('Error fetching ticket details:', error);
      }
    };

    fetchTicketDetails();
  }, [ticketId]);

  const handleSendMessage = async () => {
    if (!newMessage.trim()) return;

    try {
      const response = await fetch(`http://localhost:5000/auth/tickets/${ticketId}/chat`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
        body: JSON.stringify({ message: newMessage }),
      });

      const data = await response.json();
      if (response.ok) {
        setChatMessages((prevMessages) => [...prevMessages, data.message]);
        setNewMessage('');
      } else {
        console.error('Error sending message:', data.error);
      }
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  return (
    <div className="ticket-detail">
      {ticket && (
        <>
          <h1>Ticket: {ticket.title}</h1>
          <p>Description: {ticket.description}</p>
          <p>Estimated Duration: {ticket.duration} minutes</p>
          <div className="chat-section">
            <h2>Chat</h2>
            <div className="chat-messages">
              {chatMessages.map((msg, index) => (
                <div key={index} className={`chat-message ${msg.sender === role ? 'sent' : 'received'}`}>
                  <strong>{msg.sender}:</strong> {msg.text}
                </div>
              ))}
            </div>
            <input
              type="text"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              placeholder="Type your message"
            />
            <button onClick={handleSendMessage}>Send</button>
          </div>
        </>
      )}
    </div>
  );
};

export default TicketDetail;
