import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import io from 'socket.io-client';

let socket; // Declare socket variable globally to use in useEffect cleanup

const TicketDetail = () => {
  const { ticketId } = useParams();
  const [ticket, setTicket] = useState(null);
  const [chatMessages, setChatMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    // Initialize Socket.IO connection
    socket = io('http://localhost:5000');

    socket.on('receive_message', message => {
      if (message.ticketId === parseInt(ticketId)) { // Ensure that the message is for this ticket
        setChatMessages(prevMessages => [...prevMessages, message]);
      }
    });

    return () => {
      socket.off(); // Disconnect Socket.IO when the component unmounts
    };
  }, [ticketId]);

  useEffect(() => {
    const fetchTicketDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5000/auth/tickets/${ticketId}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const data = await response.json();
        if (response.ok) {
          setTicket(data);
        } else {
          console.error('Error fetching ticket details:', data.error);
        }
      } catch (error) {
        console.error('Error fetching ticket details:', error);
      }
    };

    fetchTicketDetails();
  }, [ticketId]);

  const handleSendMessage = () => {
    if (!newMessage.trim()) return;

    // Send message via Socket.IO
    socket.emit('send_message', {
      ticketId: parseInt(ticketId),
      text: newMessage,
      sender: 'currentUserName', // Replace with actual user info
    });

    setNewMessage('');
  };

  return (
    <div className="ticket-detail">
      {ticket && (
        <>
          <h1>Ticket: {ticket.title}</h1>
          <p>Description: {ticket.description}</p>
          <div className="chat-section">
            <h2>Chat</h2>
            <div className="chat-messages">
              {chatMessages.map((msg, index) => (
                <div key={index} className="chat-message">
                  <strong>{msg.sender}:</strong> {msg.text}
                </div>
              ))}
            </div>
            <input
              type="text"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              placeholder="Type your message here..."
            />
            <button onClick={handleSendMessage}>Send</button>
          </div>
        </>
      )}
    </div>
  );
};

export default TicketDetail;
