// src/components/ITHelperDashboard.js
import React, { useEffect, useState } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import moment from 'moment';
import './ITHelperDashboard.css';

const ITHelperDashboard = () => {
  const [tickets, setTickets] = useState([]);
  const localizer = momentLocalizer(moment);

  useEffect(() => {
    const fetchTickets = async () => {
      try {
        const response = await fetch('http://localhost:5000/auth/tickets/assigned', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setTickets(data);
        }
      } catch (error) {
        console.error('Error fetching tickets:', error);
      }
    };

    fetchTickets();
  }, []);

  const events = tickets.map(ticket => ({
    title: ticket.title,
    start: new Date(), // Simplified for demo; replace with actual time slot logic
    end: new Date(new Date().getTime() + ticket.duration * 60000), // Duration in minutes
    allDay: false,
  }));

  return (
    <div className="it-helper-dashboard">
      <h1>IT Helper Dashboard</h1>
      <div className="ticket-section">
        <h2>Assigned Tickets</h2>
        <ul>
          {tickets.map(ticket => (
            <li key={ticket.id}>
              {ticket.title} - {ticket.description} - {ticket.duration} minutes
            </li>
          ))}
        </ul>
      </div>
      <div className="calendar-section">
        <Calendar
          localizer={localizer}
          events={events}
          startAccessor="start"
          endAccessor="end"
          style={{ height: 500, margin: "50px" }}
        />
      </div>
    </div>
  );
};

export default ITHelperDashboard;
